INSERT INTO `on_news` VALUES ('59','21','私人觀覽室','','私人觀覽室','','1','以博物館標準而設計的空間，用於鑒定，展示和觀賞藝術品。 觀覽室也可以根據要求進行佈置，為私人活動增添色彩。','1740388600','1740388600','<p><img src="http://wniw.khunia.cn/Public/ueditor/php/upload/98471740388596.jpg" width="800" height="533"/><span style="font-family: &quot;Noto Sans SC&quot;, sans-serif; font-size: 17px; text-wrap: wrap; background-color: rgb(255, 255, 255);">以博物館標準而設計的空間，用於鑒定，展示和觀賞藝術品。 觀覽室也可以根據要求進行佈置，為私人活動增添色彩。</span></p>','1');
